var jqxhr=$.get("http://www.freecodecamp.com/news/hot");jqxhr.success(function(){var e=jqxhr.responseJSON;console.log(e[0].headline),e.forEach(function(e){$(".media-list").append('<li class="media"><a class="pull-left" href="'+e.link+'"><img class="media-object" data-src="holder.js/64x64" src="'+e.image+'" alt="'+e.heading+'" style="width: 64px; height: 64px;"></a><div class="media-body"><h4 class="media-heading">'+e.headline+"</h4></div>"+e.metaDescription+"</li>")})});